import React, {useState, useEffect} from 'react'
import {ScrollView, Text, StyleSheet, View, FlatList, Image, TouchableOpacity} from "react-native"
import { FontAwesome } from '@expo/vector-icons';
import AsyncStorage from '@react-native-async-storage/async-storage';


export const GameCard = ({item}) => {

    const [cartItems, setCartItems] = useState([])

    useEffect(() => {
         const setLocal = async () => {
             await AsyncStorage.setItem("game", JSON.stringify(item))

        }

        setLocal()
      }, []);

      useEffect(() => {
        const getData = async () => {
            try {
                await setCartItems(JSON.parse(AsyncStorage.getItem("game")) || []);
            } catch(e) {
            // error reading value
            }
        }

         getData()
      }, [])

      console.log("asdasdasd", [cartItems])




    // const onAdd = (product) => {
